#include <boost/multiprecision/cpp_int.hpp>
#include "test.hpp"

int main()
{
   const boost::multiprecision::cpp_int a("");
   const boost::multiprecision::cpp_int b("");
   const boost::multiprecision::cpp_int r = gcd(a, b);

   if (a % r)
      return 1;
   if (b % r)
      return 1;
   if (gcd(a / r, b / r) != 1)
      return 1;

   return 0;
}


